package fpt.edu.testdatabinding.modelimport android.text.TextUtilsimport androidx.databinding.BaseObservableimport androidx.databinding.Bindableimport fpt.edu.testdatabinding.BRclass Model : BaseObservable() {    @get:Bindable    var name: String = ""        set(value) {            field = value            notifyPropertyChanged(BR.name)        }    @get:Bindable    var pass: String = ""        set(value) {            field = value            notifyPropertyChanged(BR.pass)        }    fun isVaild(): Boolean {        return name.equals("admin") && pass.equals("admin")    }    fun emptyCheck() : Boolean{        return !TextUtils.isEmpty(name)    }}