package fpt.edu.testdatabinding.viewmodelimport android.content.Contextimport android.content.Intentimport android.widget.Toastimport androidx.databinding.BaseObservableimport androidx.databinding.ObservableFieldimport fpt.edu.testdatabinding.activity.NextActivityimport fpt.edu.testdatabinding.model.Modelclass MainViewModel : BaseObservable() {    var model = ObservableField<Model>()    fun onCreate() {        model.set(Model())    }    fun onClickCheck(context: Context) {        if (model.get()?.isVaild() == true) {//          context.Toast.makeText(this,//              "Bạn nhập sai mk và tk",//              Toast.LENGTH_SHORT//          ).show()            Toast.makeText(context, "Bạn đúng mk và tài khoản", Toast.LENGTH_SHORT).show()            load(context)        } else {            Toast.makeText(context, "Bạn sai mk và tài khoản", Toast.LENGTH_SHORT).show()        }    }    private fun load(context: Context) {        val intent = Intent(context, NextActivity::class.java)        context.startActivity(intent)    }}