package fpt.edu.testdatabinding.viewmodelimport android.content.Contextimport android.content.Intentimport androidx.databinding.BaseObservableimport fpt.edu.testdatabinding.activity.CalendarActivityimport fpt.edu.testdatabinding.activity.ShowActivityclass NextViewModel : BaseObservable() {    fun loadShow(context: Context) {        var intent = Intent(context, ShowActivity::class.java)        context.startActivity(intent)    }    fun loadGiaoDien(context: Context) {        var intent = Intent(context, CalendarActivity::class.java)        context.startActivity(intent)    }}