package fpt.edu.testdatabinding.viewmodelimport android.content.Contextimport android.widget.Toastimport androidx.databinding.BaseObservableimport androidx.databinding.ObservableFieldimport androidx.databinding.OnRebindCallbackimport fpt.edu.testdatabinding.model.Modelclass ShowViewModel : BaseObservable() {    var result = ObservableField<String>()    var showViewModel = ObservableField<Model>()    fun onCreate() {        showViewModel.set(Model())    }    fun onClickCheck(context: Context) {        if (showViewModel.get()?.emptyCheck() == true) {            3            Toast.makeText(context, "Thành công", Toast.LENGTH_SHORT).show()            result.set(showViewModel.get()?.name)            show {}        } else {            Toast.makeText(context, "Bạn chưa nhập", Toast.LENGTH_SHORT).show()        }    }    fun show(callback: (String) -> Unit) {        var n = showViewModel.get()?.name        callback.invoke("$n")    }}